/* browser.min.js */
(function() {
    var ua = navigator.userAgent,
        browser = {
            name: 'unknown',
            version: '0.0'
        };

    if (ua.indexOf('Edge') > -1) {
        browser.name = 'Edge';
        browser.version = ua.split('Edge/')[1];
    } else if (ua.indexOf('MSIE') > -1) {
        browser.name = 'Internet Explorer';
        browser.version = ua.split('MSIE ')[1].split(';')[0];
    } else if (ua.indexOf('Trident') > -1) {
        browser.name = 'Internet Explorer';
        browser.version = ua.split('rv:')[1].split(')')[0];
    } else if (ua.indexOf('Chrome') > -1) {
        browser.name = 'Chrome';
        browser.version = ua.split('Chrome/')[1].split(' ')[0];
    } else if (ua.indexOf('Safari') > -1) {
        browser.name = 'Safari';
        browser.version = ua.split('Version/')[1].split(' ')[0];
    } else if (ua.indexOf('Firefox') > -1) {
        browser.name = 'Firefox';
        browser.version = ua.split('Firefox/')[1];
    } else if (ua.indexOf('Opera') > -1) {
        browser.name = 'Opera';
        browser.version = ua.split('Opera/')[1];
    }

    document.documentElement.setAttribute('data-browser', browser.name);
    document.documentElement.setAttribute('data-browser-version', browser.version);
})();
